TwitchChatter FirstCurrency(TwitchChatter tc, vector<int> rates) {


	if (tc.old != true) {
		tc.currency += rates[4];
		tc.old = true;
	}

	return tc;
}



TwitchChatter UpdateCurrency(TwitchChatter tc, vector<int> rates) {

	if (tc.lastActive + rates[0] < time(NULL)) tc.active = false;

	if(tc.active) tc.currency+= rates[2];

	return tc;
}


