unordered_map<string, TwitchChatter> startUpScores(string path) {

	//CopyFileA((path + "\\Twitch\\chatCurrency.txt").c_str(), (path + "\\Twitch\\chatCurrencyBACKUP.txt").c_str(), false);

	unordered_map<string, TwitchChatter> m;
	std::ifstream in;
	in.open((path + "\\Twitch\\chatCurrency.txt").c_str());
	string user;
	int bank;
	TwitchChatter tc;
	while (in >> user) {
		in >> bank;
		tc.currency = bank;
		tc.name = user;
		tc.active = false;
		tc.old = true;
		m[user] = tc;
	}


	in.close();
	return m;
}

