string lowerCase(string data) {
	std::transform(data.begin(), data.end(), data.begin(),
		[](unsigned char c) { return tolower(c); });
	return data;
}

TwitchMsg parseChat(string s) {
	TwitchMsg msg;
	s = lowerCase(s);


	s = s.substr(s.find(":") + 1);
	string u = s.substr(0, s.find("!"));
	msg.user = u;
	s = s.substr(s.find(":") + 1);
	s = s.substr(0, s.find("\n") - 1);
	msg.msg = s;
	return msg;
}

int sizeOfArray(int* arr) {
	int s = 0;
	while (*arr != -1) {
		arr++;
		s++;
	}
	return s;
}



